    <div className="chat">
      <div className="messages">
        {messages.map((msg, index) => (
          <div>
          <Message key={index} text={input}/>
          {/* <Message key={index} text={msg} /> */}
          </div>
        ))}
      </div>
      <input
        type="text"
        value={input}
        onChange={(e) => setInput(e.target.value)}
        placeholder="Type a message..."
      />
      
      <button onClick={sendMessage}>Send</button>
    </div>
